import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './components/Home';
import EventsSection from './components/EventsSection';
import Login from './components/Login';
import SignUp from './components/SignUp';
import Tickets from './components/Tickets';
import Profile from './components/Profile'; // Import the Profile component
import ListTicket from './components/ListTicket'; // Import the Profile component

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem('jwtToken');
        if (token) {
            setIsLoggedIn(true);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('jwtToken');
        localStorage.removeItem('userId');
        setIsLoggedIn(false);
    };

    return (
        <Router>
            <div className="App">
                <Navbar isLoggedIn={isLoggedIn} handleLogout={handleLogout} />
                <main>
                    <Routes>
                        <Route path="/" element={<Home />} />
                        <Route path="/events" element={<EventsSection isLoggedIn={isLoggedIn} />} />
                        <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
                        <Route path="/signup" element={<SignUp setIsLoggedIn={setIsLoggedIn} />} />
                        <Route path="/list-ticket" element={<ListTicket />} /> {/* New route for List Ticket */}
                        <Route
                            path="/tickets"
                            element={isLoggedIn ? <Tickets /> : <Navigate to="/login" />}
                        />
                        <Route
                            path="/profile"
                            element={isLoggedIn ? <Profile /> : <Navigate to="/login" />} // Protect profile route
                        />
                    </Routes>
                </main>
                <Footer />
            </div>
        </Router>
    );
}

export default App;
