import React, { useState } from 'react';
import { AlertCircle } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';

const ListTicket = () => {
    const [numberOfTickets, setNumberOfTickets] = useState(1);
    const [seatInfo, setSeatInfo] = useState([{ seatNumber: '', block: '', level: '' }]);
    const [eventDate, setEventDate] = useState('');
    const [price, setPrice] = useState('');
    const [deadline, setDeadline] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleNumberOfTicketsChange = (e) => {
        const newNumberOfTickets = parseInt(e.target.value);
        setNumberOfTickets(newNumberOfTickets);

        const newSeatInfo = Array.from({ length: newNumberOfTickets }, (_, i) => ({
            seatNumber: seatInfo[i]?.seatNumber || '',
            block: seatInfo[i]?.block || '',
            level: seatInfo[i]?.level || ''
        }));
        setSeatInfo(newSeatInfo);
    };

    const handleSeatChange = (index, field, value) => {
        const updatedSeatInfo = [...seatInfo];
        updatedSeatInfo[index][field] = value;
        setSeatInfo(updatedSeatInfo);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Implement form submission logic here
        console.log('Form submitted');
    };

    return (
        <div className="max-w-2xl mx-auto p-6 bg-white rounded-xl shadow-md">
            <h2 className="text-3xl font-bold text-center mb-8">List Your Ticket</h2>
            <form onSubmit={handleSubmit} className="space-y-6">
                <div className="space-y-4">
                    <label className="block">
                        <span className="text-gray-700">Event Date</span>
                        <input
                            type="text"
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                            placeholder="Enter event date in dd/mm/yyyy format"
                            value={eventDate}
                            onChange={(e) => setEventDate(e.target.value)}
                            required
                        />
                    </label>

                    <label className="block">
                        <span className="text-gray-700">Number of Tickets</span>
                        <select
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                            value={numberOfTickets}
                            onChange={handleNumberOfTicketsChange}
                            required
                        >
                            {[1, 2, 3, 4].map((num) => (
                                <option key={num} value={num}>
                                    {num}
                                </option>
                            ))}
                        </select>
                    </label>

                    {seatInfo.map((seat, index) => (
                        <div key={index} className="grid grid-cols-3 gap-4">
                            <label className="block">
                                <span className="text-gray-700">Seat Number {index + 1}</span>
                                <input
                                    type="text"
                                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                                    value={seat.seatNumber}
                                    onChange={(e) => handleSeatChange(index, 'seatNumber', e.target.value)}
                                    required
                                />
                            </label>
                            <label className="block">
                                <span className="text-gray-700">Block</span>
                                <input
                                    type="text"
                                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                                    value={seat.block}
                                    onChange={(e) => handleSeatChange(index, 'block', e.target.value)}
                                    required
                                />
                            </label>
                            <label className="block">
                                <span className="text-gray-700">Level</span>
                                <input
                                    type="number"
                                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                                    value={seat.level}
                                    onChange={(e) => handleSeatChange(index, 'level', e.target.value)}
                                    required
                                />
                            </label>
                        </div>
                    ))}

                    <label className="block">
                        <span className="text-gray-700">Looking to Sell for (₹)</span>
                        <input
                            type="number"
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                            value={price}
                            onChange={(e) => setPrice(e.target.value)}
                            required
                        />
                    </label>

                    <label className="block">
                        <span className="text-gray-700">Auction End Date</span>
                        <input
                            type="text"
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                            placeholder="Enter auction end date in dd/mm/yyyy format"
                            value={deadline}
                            onChange={(e) => setDeadline(e.target.value)}
                            required
                        />
                    </label>
                </div>

                {errorMessage && (
                    <Alert variant="destructive">
                        <AlertCircle className="h-4 w-4" />
                        <AlertDescription>{errorMessage}</AlertDescription>
                    </Alert>
                )}

                <button
                    type="submit"
                    className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                >
                    List Ticket
                </button>
            </form>
        </div>
    );
};

export default ListTicket;
