import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import EventsSection from './components/EventsSection';
import Login from './components/Login';
import SignUp from './components/SignUp';
import Tickets from './components/Tickets';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    // Check for JWT token in localStorage on component mount
    useEffect(() => {
        const token = localStorage.getItem('jwtToken');
        if (token) {
            setIsLoggedIn(true);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('jwtToken'); // Remove the token from localStorage
        localStorage.removeItem('userId'); // Optionally remove userId too
        setIsLoggedIn(false);
    };

    return (
        <Router>
            <div className="App">
                <Navbar isLoggedIn={isLoggedIn} handleLogout={handleLogout} />
                <main>
                    <Routes>
                        <Route path="/" element={<EventsSection isLoggedIn={isLoggedIn} />} />
                        <Route path="/events" element={<EventsSection isLoggedIn={isLoggedIn} />} />
                        <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
                        <Route path="/signup" element={<SignUp setIsLoggedIn={setIsLoggedIn} />} />
                        <Route
                            path="/tickets"
                            element={isLoggedIn ? <Tickets /> : <Navigate to="/login" />}
                        />
                    </Routes>
                </main>
                <Footer />
            </div>
        </Router>
    );
}

export default App;
