import React, { useEffect, useState } from 'react';
import config from '../config';
import './EventsSection.css';
import Modal from './Modal'; // Import a modal component

function EventsSection({ isLoggedIn }) {
    const [tickets, setTickets] = useState([]);
    const [view, setView] = useState('block');
    const [blockFilter, setBlockFilter] = useState('');
    const [levelFilter, setLevelFilter] = useState('');
    const [sortOption, setSortOption] = useState('deadline');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedTicket, setSelectedTicket] = useState(null); // To track selected ticket for modal
    const [isModalOpen, setIsModalOpen] = useState(false); // Track modal state

    useEffect(() => {
        if (isLoggedIn) {
            fetchTickets();
        }
    }, [isLoggedIn]);

    const fetchTickets = async () => {
        const token = localStorage.getItem('jwtToken');
        if (!token) {
            console.error('No token found');
            setError('No token found');
            setLoading(false);
            return;
        }

        try {
            const response = await fetch(`${config.API_BASE_URL}/get-all-tickets`, {
                method: 'POST',
                headers: {
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    limit: 100,
                    offset: 0,
                }),
            });

            if (!response.ok) {
                throw new Error(`Failed to fetch tickets. Status: ${response.status}`);
            }

            const data = await response.json();
            setTickets(data.Tickets);
        } catch (error) {
            console.error('Error fetching tickets:', error);
            setError('Failed to fetch tickets.');
        } finally {
            setLoading(false);
        }
    };

    const handleViewDetails = (ticket) => {
        setSelectedTicket(ticket);
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setSelectedTicket(null);
    };

    const renderTickets = () => {
        if (loading) {
            return <p>Loading tickets...</p>;
        }

        if (error) {
            return <p>Error: {error}</p>;
        }

        return tickets.map((ticket, index) => (
            <div key={index} className={view === 'block' ? 'ticket-block' : 'ticket-list'}>
                <h3>Coldplay Music of The Spheres World Tour</h3>
                <p>Date: {new Date(ticket.eventDate).toLocaleDateString()}</p>
                <p>Price: ₹{ticket.price}</p>
                <p>Seats: {ticket.seatInfo.map(seat => `#${seat.seatNumber} (Block: ${seat.block}, Level: ${seat.level})`).join(', ')}</p>
                <p>Time Left: {calculateTimeLeft(ticket.deadline)}</p>
                <button onClick={() => handleViewDetails(ticket)}>View Details</button> {/* View Details button */}
            </div>
        ));
    };

    const calculateTimeLeft = (deadline) => {
        const difference = new Date(deadline) - new Date();
        const days = Math.floor(difference / (1000 * 60 * 60 * 24));
        const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
        const minutes = Math.floor((difference / 1000 / 60) % 60);
        const seconds = Math.floor((difference / 1000) % 60);
        return `${days}d ${hours}h ${minutes}m ${seconds}s`;
    };

    return (
        <div className="events-section">
            <h2>Coldplay Music of The Spheres World Tour</h2>

            {/* Render Tickets */}
            <div className={`ticket-container ${view}`}>
                {renderTickets()}
            </div>

            {/* Modal for viewing ticket details */}
            {isModalOpen && selectedTicket && (
                <Modal closeModal={closeModal}>
                    <h2>Ticket Details</h2>
                    <p><strong>Date:</strong> {new Date(selectedTicket.eventDate).toLocaleDateString()}</p>
                    <p><strong>Price:</strong> ₹{selectedTicket.price}</p>
                    <p><strong>Seats:</strong> {selectedTicket.seatInfo.map(seat => `#${seat.seatNumber} (Block: ${seat.block}, Level: ${seat.level})`).join(', ')}</p>
                    <p><strong>Time Left:</strong> {calculateTimeLeft(selectedTicket.deadline)}</p>
                    <p><strong>Listed By:</strong> {selectedTicket.listedBy}</p>
                    <button onClick={closeModal}>Close</button>
                </Modal>
            )}
        </div>
    );
}

export default EventsSection;
