import React from 'react';
import './EventsSection.css'; // We will style the card here

function EventsSection({ isLoggedIn }) {
    const handleEventClick = () => {
        if (isLoggedIn) {
            // Redirect to tickets if logged in
            window.location.href = '/tickets';
        } else {
            // Redirect to login if not logged in
            window.location.href = '/login';
        }
    };

    return (
        <div className="events-section">
            <h2>Upcoming Events</h2>
            <div className="event-card" onClick={handleEventClick}>
                <div className="event-image-container">
                    <img src={sampleImage} alt="Coldplay Event" className="event-image" />
                </div>
                <div className="event-details">
                    <h3>Coldplay Music of The Spheres World Tour</h3>
                    <p>DY Patil Stadium, Nerul, Navi Mumbai</p>
                    <p>January 18, 19, 21, 2025</p>
                    <p className="ticket-link">Click to show ticket listing form</p>
                </div>
            </div>
        </div>
    );
}

export default EventsSection;
